import React, { useState } from 'react';
import axios from 'axios';
import './UploadForm.css'; // Assuming you have custom styles

function UploadForm() {
    const [animalInfo, setAnimalInfo] = useState(null);
    const [error, setError] = useState('');
    const [isDragging, setIsDragging] = useState(false);

    const handleFileChange = (event) => {
        if (event.target.files.length > 0) {
            const file = event.target.files[0];
            const formData = new FormData();
            formData.append('file', file);
            setError('');
            handleFileUpload(formData);
        } else {
            setError('Please select a valid file.');
        }
    };

    const handleFileUpload = async (formData) => {
        try {
            const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setAnimalInfo(response.data);
        } catch (error) {
            setError('Failed to upload file or retrieve animal information.');
            console.error('Error uploading file:', error);
        }
    };

    const handleDragOver = (event) => {
        event.preventDefault();
        setIsDragging(true);
    };

    const handleDragLeave = () => {
        setIsDragging(false);
    };

    const handleDrop = (event) => {
        event.preventDefault();
        setIsDragging(false);
        if (event.dataTransfer.files.length > 0) {
            const file = event.dataTransfer.files[0];
            const formData = new FormData();
            formData.append('file', file);
            setError('');
            handleFileUpload(formData);
        } else {
            setError('Please drop a valid file.');
        }
    };

    return (
        <div className="upload-form-container">
            <h1 className="title">Animal Identifier</h1>

            <div
                className={`dropzone ${isDragging ? 'dragging' : ''}`}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
            >
                <p>Drag & Drop your image here, or</p>
                <input
                    type="file"
                    className="file-input"
                    onChange={handleFileChange}
                    style={{ display: 'none' }}
                    id="file-upload"
                />
                <label htmlFor="file-upload" className="upload-button">
                    Browse
                </label>
            </div>

            {error && <p className="error">{error}</p>}

            {animalInfo && (
                <div className="animal-info">
                    <h2 className="animal-name">{animalInfo.animal}</h2>
                    <p><strong>Scientific Name:</strong> {animalInfo.info[0]?.taxonomy?.scientific_name}</p>
                    <p><strong>Common Name:</strong> {animalInfo.info[0]?.name}</p>
                    <p><strong>Locations:</strong> {animalInfo.info[0]?.locations.join(', ')}</p>
                    <p><strong>Diet:</strong> {animalInfo.info[0]?.characteristics?.diet}</p>
                    <p><strong>Lifespan:</strong> {animalInfo.info[0]?.characteristics?.lifespan}</p>
                    {/* Add more fields as needed */}
                </div>
            )}
        </div>
    );
}

export default UploadForm;
