import React, { useState } from 'react';
import { uploadImage } from '../api/api';
import AnimalInfo from './AnimalInfo';
import { Box, Button, CircularProgress, Typography, Card, CardContent, Paper } from '@mui/material';
import { useDropzone } from 'react-dropzone';

const UploadForm = () => {
    const [file, setFile] = useState(null);
    const [animalInfo, setAnimalInfo] = useState(null);
    const [loading, setLoading] = useState(false);

    const onDrop = (acceptedFiles) => {
        const selectedFile = acceptedFiles[0];
        setFile(selectedFile);
        console.log("Selected file:", selectedFile);  // Verify file selection
    };

    const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop, accept: 'image/*', maxFiles: 1 });

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!file) {
            console.error("No file selected!");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await uploadImage(formData);
            setAnimalInfo(response.data);
            console.log("Upload successful:", response.data);
        } catch (error) {
            console.error('Error uploading image', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 4 }}>
            <Typography variant="h4" gutterBottom>
                Animal Image Prediction
            </Typography>
            <Card sx={{ minWidth: 300, maxWidth: 600, p: 2, mt: 2 }}>
                <CardContent>
                    <Paper
                        {...getRootProps()}
                        sx={{
                            p: 4,
                            textAlign: 'center',
                            border: '2px dashed',
                            borderColor: isDragActive ? 'primary.main' : 'text.secondary',
                            backgroundColor: isDragActive ? 'primary.light' : 'background.paper',
                            cursor: 'pointer'
                        }}
                    >
                        <input {...getInputProps()} />
                        {isDragActive ? (
                            <Typography variant="h6">Drop the image here...</Typography>
                        ) : (
                            <Typography variant="h6">Drag 'n' drop an image, or click to select one</Typography>
                        )}
                        {file && (
                            <Typography variant="body1" sx={{ mt: 2 }}>
                                Selected File: {file.name}
                            </Typography>
                        )}
                    </Paper>
                    <Box sx={{ mt: 2, textAlign: 'center' }}>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={handleSubmit}
                            disabled={loading || !file}
                        >
                            {loading ? <CircularProgress size={24} /> : 'Upload Image'}
                        </Button>
                    </Box>
                </CardContent>
            </Card>
            {animalInfo && <AnimalInfo info={animalInfo} />}
        </Box>
    );
};

export default UploadForm;
