import React, { useState } from 'react';
import axios from 'axios';

const UploadForm = () => {
    const [file, setFile] = useState(null);
    const [animalInfo, setAnimalInfo] = useState(null);
    const [error, setError] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setAnimalInfo(null);  // Clear previous results when a new file is uploaded
        setError('');
    };

    const handleUpload = async () => {
        if (!file) {
            setError('Please select a file first.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setAnimalInfo(response.data);
        } catch (err) {
            setError('Failed to upload file or retrieve animal information.');
        }
    };

    return (
        <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px', borderRadius: '8px', backgroundColor: '#f9f9f9', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
            <h2 style={{ textAlign: 'center' }}>Upload Animal Image</h2>
            <input type="file" onChange={handleFileChange} style={{ display: 'block', margin: '20px 0' }} />
            <button onClick={handleUpload} style={{ display: 'block', width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
                Upload Image
            </button>

            {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

            {animalInfo && (
                <div style={{ marginTop: '20px' }}>
                    <h3 style={{ textAlign: 'center', marginBottom: '20px' }}>Animal Information</h3>
                    <div style={{ padding: '10px', backgroundColor: '#ffffff', borderRadius: '4px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
                        <p><strong>Name:</strong> {animalInfo.animal}</p>
                        {animalInfo.info && (
                            <>
                                {animalInfo.info.taxonomy && (
                                    <p><strong>Scientific Name:</strong> {animalInfo.info.taxonomy.scientific_name}</p>
                                )}
                                {animalInfo.info.characteristics && (
                                    <>
                                        <p><strong>Common Name:</strong> {animalInfo.info.characteristics.common_name}</p>
                                        <p><strong>Diet:</strong> {animalInfo.info.characteristics.diet}</p>
                                        <p><strong>Habitat:</strong> {animalInfo.info.characteristics.habitat}</p>
                                        <p><strong>Lifespan:</strong> {animalInfo.info.characteristics.lifespan}</p>
                                        <p><strong>Top Speed:</strong> {animalInfo.info.characteristics.top_speed}</p>
                                        {/* Add more fields as necessary */}
                                    </>
                                )}
                            </>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default UploadForm;
