import React, { useState } from 'react';
import { uploadImage } from '../api/api';
import AnimalInfo from './AnimalInfo';
import { Box, Button, CircularProgress, Typography, Card, CardContent } from '@mui/material';

const UploadForm = () => {
    const [file, setFile] = useState(null);
    const [animalInfo, setAnimalInfo] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        setFile(selectedFile);
        console.log("Selected file:", selectedFile);  // Verify file selection
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!file) {
            console.error("No file selected!");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await uploadImage(formData);
            setAnimalInfo(response.data);
            console.log("Upload successful:", response.data);
        } catch (error) {
            console.error('Error uploading image', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 4 }}>
            <Typography variant="h4" gutterBottom>
                Animal Image Prediction
            </Typography>
            <Card sx={{ minWidth: 300, maxWidth: 600, p: 2, mt: 2, textAlign: 'center' }}>
                <CardContent>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="file"
                            onChange={handleFileChange}
                            accept="image/*"
                            style={{ display: 'none' }}
                            id="upload-button"
                        />
                        <label htmlFor="upload-button">
                            <Button variant="contained" component="span">
                                Choose File
                            </Button>
                        </label>
                        <Button
                            type="submit"
                            variant="contained"
                            color="primary"
                            sx={{ ml: 2 }}
                            disabled={loading || !file}
                        >
                            {loading ? <CircularProgress size={24} /> : 'Upload Image'}
                        </Button>
                    </form>
                    {file && (
                        <Typography variant="body1" sx={{ mt: 2 }}>
                            Selected File: {file.name}
                        </Typography>
                    )}
                </CardContent>
            </Card>
            {animalInfo && <AnimalInfo info={animalInfo} />}
        </Box>
    );
};

export default UploadForm;
