import React, { useState } from 'react';
import axios from 'axios';

const UploadForm = () => {
    const [file, setFile] = useState(null);
    const [animalInfo, setAnimalInfo] = useState(null);
    const [error, setError] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setAnimalInfo(null);  // Clear previous results when a new file is uploaded
        setError('');
    };

    const handleUpload = async () => {
        if (!file) {
            setError('Please select a file first.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setAnimalInfo(response.data);
        } catch (err) {
            setError('Failed to upload file or retrieve animal information.');
        }
    };

    return (
        <div>
            <h2>Upload Animal Image</h2>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUpload}>Upload Image</button>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {animalInfo && (
                <div>
                    <h3>Animal Information</h3>
                    <p><strong>Name:</strong> {animalInfo.animal}</p>
                    <p><strong>Scientific Name:</strong> {animalInfo.info.taxonomy.scientific_name}</p>
                    <p><strong>Common Name:</strong> {animalInfo.info.characteristics.common_name}</p>
                    <p><strong>Diet:</strong> {animalInfo.info.characteristics.diet}</p>
                    <p><strong>Habitat:</strong> {animalInfo.info.characteristics.habitat}</p>
                    <p><strong>Lifespan:</strong> {animalInfo.info.characteristics.lifespan}</p>
                    <p><strong>Top Speed:</strong> {animalInfo.info.characteristics.top_speed}</p>
                    {/* Add more fields as necessary */}
                </div>
            )}
        </div>
    );
};

export default UploadForm;
